#include <stdio.h>

int main(){
	printf("%d*%d=%d\n", 2, 1, 2*1);
	printf("%d*%d=%d\n", 2, 2, 2*2);
	printf("%d*%d=%d\n", 2, 3, 2*3);
	printf("%d*%d=%d\n", 2, 4, 2*4);
	printf("%d*%d=%d\n", 2, 5, 2*5);
	printf("%d*%d=%d\n", 2, 6, 2*6);
	printf("%d*%d=%d\n", 2, 7, 2*7);
	printf("%d*%d=%d\n", 2, 8, 2*8);
	printf("%d*%d=%d\n", 2, 9, 2*9);
	return 0;
}

